INSERT INTO _content_nuxtcontent VALUES ('nuxtcontent/nuxt/nuxtcontent/vi/8.advanced/6.custom-source.md', 'Custom Source', '{"type":"minimark","value":[["p",{},"Theo mặc định, Nuxt Content cung cấp một số nguồn tích hợp như nguồn tệp cục bộ và nguồn Github từ xa. Tuy nhiên điều này không đủ cho một số trường hợp, ví dụ, bạn muốn lấy dữ liệu từ một API từ xa. Trong trường hợp này, bạn có thể định nghĩa một nguồn tùy chỉnh để lấy dữ liệu và sử dụng nó trong các bộ sưu tập của bạn."],["p",{},"Sử dụng ",["code",{},"defineCollectionSource"],", bạn có thể định nghĩa một nguồn tùy chỉnh."],["pre",{"className":"language-ts shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"import { defineCollectionSource } from ''@nuxt/content''\n\nconst hackernewsSource = defineCollectionSource({\n  getKeys: () => {\n    return fetch(''https://hacker-news.firebaseio.com/v0/topstories.json'')\n      .then(res => res.json())\n      .then(data => data.map((key: string) => `${key}.json`))\n  },\n  getItem: (key: string) => {\n    const id = key.split(''.'')[0]\n    return fetch(`https://hacker-news.firebaseio.com/v0/item/${id}.json`)\n      .then(res => res.json())\n  },\n})\n","language":"ts","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s8R28"},"import"],["span",{"class":"sDfIl"}," {"],["span",{"class":"sZSNi"}," defineCollectionSource"],["span",{"class":"sDfIl"}," }"],["span",{"class":"s8R28"}," from"],["span",{"class":"sDfIl"}," ''"],["span",{"class":"sGFVr"},"@nuxt/content"],["span",{"class":"sDfIl"},"''\n"]],["span",{"class":"line","line":2},["span",{"emptyLinePlaceholder":true},"\n"]],["span",{"class":"line","line":3},["span",{"class":"smZ93"},"const"],["span",{"class":"sZSNi"}," hackernewsSource "],["span",{"class":"sDfIl"},"="],["span",{"class":"s3cPz"}," defineCollectionSource"],["span",{"class":"sZSNi"},"("],["span",{"class":"sDfIl"},"{\n"]],["span",{"class":"line","line":4},["span