INSERT INTO _content_nuxtcontent VALUES ('nuxtcontent/nuxt/nuxtcontent/vi/9.studio/1.setup.md', 'Setup Nuxt Studio', '{"type":"minimark","value":[["p",{},"Studio là giao diện CMS trực quan để chỉnh sửa các trang web Nuxt Content của bạn."],["p",{},"Nó tận dụng ",["code",{},"Preview API"]," có trong Nuxt Content để đề xuất trải nghiệm chỉnh sửa tốt nhất cho các tệp nội dung của bạn. Các biên tập viên có thể hưởng lợi từ giao diện thân thiện với người dùng để chỉnh sửa các tệp ",["code",{},"Markdown"],", ",["code",{},"YAML"]," hoặc ",["code",{},"JSON"]," của họ."],["p",{},"Các nhà phát triển có thể tùy chỉnh trải nghiệm chỉnh sửa và cung cấp công cụ cho biên tập viên tập trung vào nội dung mà không yêu cầu chuyên môn kỹ thuật."],["video",{":autoplay":"true",":controls":"true",":loop":"true","poster":"/home/videos/HomeNotionLikePoster.webp","src":"https://res.cloudinary.com/nuxt/video/upload/v1733494722/contentv3final_rc8bvu.mp4"}],["h2",{"id":"authentication"},"Authentication"],["p",{},"Quản trị viên Studio nằm trên ",["a",{"href":"https://nuxt.studio","rel":["nofollow"]},"nuxt.studio"],". Từ đó, bạn có thể đăng nhập bằng GitHub hoặc Google. Cả hai phương pháp đều cho bạn cùng quyền chỉnh sửa nhưng vì Studio được ",["a",{"href":"/docs/studio/github"},"đồng bộ hóa với GitHub"],", việc nhập kho lưu trữ phải được xử lý bởi người dùng GitHub."],["warning",{},["p",{},"Xác thực Google được điều chỉnh cho người dùng không chuyên. Người dùng Google phải tham gia một nhóm với các dự án hiện có để chỉnh sửa chúng."]],["h2",{"id":"connect-your-nuxt-content-repository"},"Connect your Nuxt Content Repository"],["p",{},"Sau khi đăng nhập vào quản trị viên, bạn có thể nhập kho lưu trữ Nuxt Content của mình từ giao diện."],["tip",{},["p",{},"Có hai tùy chọn khả dụng, bạn có thể nhập một kho lưu trữ hiện có hoặc sử dụng một trong các ",["a",{"href":"/templates"},"mẫu"]," của chúng tôi."]],["p",{},"Yêu cầu duy nhất là lưu trữ kho lưu trữ của bạn trên GitHub. Từ giao diện, kết nối với ",["a",{"href":"/docs/studio/github"},"ứng dụng GitHub"]," của chúng tôi, sau đó bạn sẽ có thể liên kết kho lưu trữ GitHub với dự án Studio của mình."],["p",{},"Sau khi dự án được tạo, bạn có thể bắt đầu chỉnh sửa các tệp của mình với các biên tập viên Studio và xuất bản các thay đổi của bạn."],["h2",{"id":"enable-the-full-editing-experience"},["strong",{},"Enable the Full Editing Experience"]],["p",{},"Để mở khóa phạm vi đầy đủ các tính năng trong Studio, hãy đảm bảo URL dự án của bạn được cấu hình đúng cách trong phần triển khai."],["p",{},["strong",{},"Điều này sẽ kích hoạt tất cả các tính năng Studio bao gồm:"]],["ul",{},["li",{},["strong",{},"Xem trước trực tiếp trang web của bạn:"]," ngay lập tức thấy các thay đổi được phản ánh trên trang web của bạn khi bạn làm việc."],["li",{},["strong",{},"Tạo biểu mẫu động:"]," Tự động tạo biểu mẫu để chỉnh sửa các tệp của bạn dựa trên ",["a",{"href":"/docs/collections/define"},"bộ sưu tập"]," Nuxt Content của bạn."],["li",{},["strong",{},"Biên tập viên markdown nâng cao:"]," tận hưởng trải nghiệm chỉnh sửa được cải thiện, bao gồm khả năng liệt kê và tích hợp ",["a",{"href":"/docs/studio/content#vue-components"},"thành phần Vue"]," mà bạn đã hiển thị."],["li",{},["strong",{},"Cấu hình ứng dụng tự động:"]," tạo ",["a",{"href":"/docs/studio/config"},"cấu hình ứng dụng"]," dựa trên lược đồ bạn đã định nghĩa."]],["tip",{},["p",{},"Chúng tôi đang đề xuất triển khai GitHub Pages. Bằng cách sử dụng nó, chúng tôi sẽ xử lý tất cả các yêu cầu cho bạn. Tuy nhiên, nếu bạn muốn sử dụng nền tảng triển khai theo lựa chọn của mình, vui lòng làm theo hướng dẫn đơn giản bên dưới."]],["p",{},"Để có thể đặt URL của bạn trong Studio, bạn chỉ cần đặt ",["code",{},"Preview API"]," trong cấu hình nội dung của tệp ",["code",{},"nuxt.config.ts"]," của bạn."],["pre",{"className":"language-ts shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"export default defineNuxtConfig({\n  content: {\n    preview: 