INSERT INTO _content_nuxtcontent VALUES ('nuxtcontent/nuxt/nuxtcontent/vi/9.studio/4.medias.md', 'Manage and integrate Medias in Nuxt Content Studio CMS', '{"type":"minimark","value":[["h2",{"id":"browse-your-medias"},"Browse your medias"],["p",{},"Tất cả phương tiện nằm trong thư mục ",["code",{},"/public"]," đều có sẵn trong tab ",["strong",{},"Media"]," của nền tảng Studio."],["p",{},["img",{"alt":"Media gallery on Studio","src":"/docs/studio/editors-medias.webp"}]],["p",{},"Đó là giao diện trực quan cho người dùng không chuyên để quản lý thư mục ",["code",{},"/public"]," của họ."],["p",{},"Người dùng có thể dễ dàng duyệt các thư mục, tải lên phương tiện mới ở bất kỳ cấp nào, và kéo thả phương tiện vào các thư mục khác, làm cho việc tổ chức phương tiện trở nên đơn giản."],["p",{},"Giao diện được thiết kế để trực quan cho người dùng không chuyên. Nó có thể được xem như một IDE thân thiện với người dùng."],["h2",{"id":"use-it-in-the-notion-like-editor"},"Use it in the Notion-like editor"],["p",{},"Người dùng có thể đơn giản kéo và thả hình ảnh trực tiếp vào biên tập viên. Một modal tải lên sẽ mở để cho bạn chọn thư mục đích."],["p",{},"Bằng cách gõ ",["code",{},"/"]," và tìm kiếm ",["code",{},"Image"],", họ có thể nhanh chóng chèn một phương tiện. Một modal sẽ mở để cho họ chọn phương tiện họ muốn chèn."],["p",{},"Từ modal phương tiện, bạn có thể đặt ",["a",{"href":"https://www.w3schools.com/tags/att_img_alt.asp","rel":["nofollow"]},"thuộc tính alt"]," cho mục đích SEO và khả năng truy cập."]],"toc":{"title":"","searchDepth":2,"depth":2,"links":[{"id":"browse-your-medias","depth":2,"text":"Browse your medias"},{"id":"use-it-in-the-notion-like-editor","depth":2,"text":"Use it in the Notion-like editor"}]}}', 'Explore how to browse and manage media files, and integrate them into your projects using Nuxt Content Studio CMS features.', 'md', NULL, '{}', '{"title":"Media Library"}', '/nuxt/nuxtcontent/vi/studio/medias', '{"title":"Manage and integrate Medias in Nuxt Content Studio CMS","description":"Explore how to browse and manage media files, and integrate them into your projects using Nuxt Content Studio CMS features."}', 'nuxt/nuxtcontent/vi/9.studio/4.medias', NULL, 'YteL2ZufrulfGmghPCHOoRqSZsblCQqefD0EbUfLw8Y'); -- YteL2ZufrulfGmghPCHOoRqSZsblCQqefD0EbUfLw8Y
INSERT INTO _content_nuxtcontent VALUES ('nuxtcontent/nuxt/nuxtcontent/vi/9.studio/5.config.md', 'Tailor application configuration edition', '{"type":"minimark","value":[["p",{},"Khi vào tab ",["strong",{},"Config"]," của biên tập viên, bạn có thể duyệt các cấu hình để tùy chỉnh trang web của mình. Các cấu hình này đại diện cho cài đặt được định nghĩa trong tệp ",["code",{},"app.config.ts"]," của bạn."],["h2",{"id":"appconfigts"},["code",{},"app.config.ts"]],["p",{},"Tệp ",["a",{"href":"https://nuxt.com/docs/guide/directory-structure/app-config","rel":["nofollow"]},["code",{},"app.config.ts"]]," là tệp cấu hình được giới thiệu trong Nuxt 3. Đó là tệp TypeScript cho phép bạn cấu hình các khía cạnh khác nhau của cài đặt ứng dụng của bạn. Các nhà phát triển có thể dễ dàng biến bất kỳ trang web nào thành trải nghiệm có thể cấu hình bằng tệp này."],["h2",{"id":"customize-edition"},"Customize edition"],["prose-note",{},["p",{},"Đảm bảo bạn có ít nhất một tệp cấu hình trống trong ứng dụng của bạn."],["pre",{"className":"language-ts shiki shiki-themes material-theme-lighter material-theme-lighter material-theme-palenight","code":"export default defineAppConfig({})\n","filename":"app.config.ts","language":"ts","meta":"","style":""},["code",{"__ignoreMap":""},["span",{"class":"line","line":1},["span",{"class":"s8R28"},"export"],["span",{"class":"s8R28"}," default"],["span",{"class":"s3cPz"}," defineAppConfig"],["span",{"class":"sZSNi"},"("],["span",{"class":"sDfIl"},"{}"],["span",{"class":"sZSNi"},")\n"]]]]],["p",{},"Để tạo trải nghiệm chỉnh sửa tùy chỉnh cho ",["code",{},"app.config.ts"]," của bạn trong Studio, bạn cần tạo tệp ",["code",{},"nuxt.schema.ts"]," trong dự án của bạn. Lược đồ này phục vụ như một biểu diễn của ",["code",{},"app.config.ts"]," của bạn."],["h3",{"id":"helpers"},"Helpers"],["prose-note",{},["p",{},"Những trợ giúp này được cung cấp bởi Nuxt Content ",["code",{},"Preview API"],"."]],["ul",{},["li",{},"Phương thức ",["code",{},"group"]," cho phép bạn tùy