UPDATE _content_info SET ready = true WHERE id = 'checksum_nuxtcontent'; -- meta
CREATE TABLE IF NOT EXISTS _content_info (id TEXT PRIMARY KEY, "ready" BOOLEAN, "structureVersion" VARCHAR, "version" VARCHAR, "__hash__" TEXT UNIQUE); -- structure
DROP TABLE IF EXISTS _content_nuxtui; -- structure
CREATE TABLE IF NOT EXISTS _content_nuxtui (id TEXT PRIMARY KEY, "title" VARCHAR, "body" TEXT, "category" VARCHAR NULL, "description" VARCHAR, "extension" VARCHAR, "framework" VARCHAR NULL, "links" TEXT, "meta" TEXT, "module" VARCHAR NULL, "navigation" TEXT, "path" VARCHAR, "seo" TEXT DEFAULT '{}', "stem" VARCHAR, "__hash__" TEXT UNIQUE); -- structure
